mapper.update:
UPDATE t_order SET state = #{state} ,
                    tracking_num = #{trackingNum},
                    express_company = #{expressCompany},
                    updated_at = #{updatedAt}
                    WHERE order_id = #{orderId}

UPDATE t_order SET comment = #{comment},
                    state = #{state},
                    updated_at = #{updatedAt}
                    WHERE order_id = #{orderId}
